mapper.insert:

SELECT ATT_SEQ.NEXTVAL FROM DUAL

INSERT ALL

 INTO ATTACH(
	ATT_GROUP_NO
	, ATT_NO
	, ATT_TYPE
	, ORIGN_NAME
	, SAVE_NAME
	, ATT_SIZE
	, ATT_DATE
	, ATT_PATH
)VALUES(
	#{attGroupNo, jdbcType=NUMERIC}
	, #{idx}+1
	, #{attach.attType, jdbcType=VARCHAR}
	, #{attach.orignName, jdbcType=VARCHAR}
	, #{attach.saveName, jdbcType=VARCHAR}
	, #{attach.attSize, jdbcType=VARCHAR}
	, SYSDATE
	, #{attach.attPath, jdbcType=VARCHAR}
)

SELECT * FROM DUAL

mapper.update:
UPDATE ATTACH SET 
    ATT_TYPE=#{attType, jdbcType=VARCHAR}
    ,ORIGN_NAME=#{orignName, jdbcType=VARCHAR}
    ,SAVE_NAME=#{saveName, jdbcType=VARCHAR}
    ,ATT_SIZE=#{attSize, jdbcType=VARCHAR}
    ,ATT_DATE= sysdate
    ,ATT_PATH=#{attPath, jdbcType=VARCHAR}
WHERE ATT_GROUP_NO=#{attGroupNo, jdbcType=NUMERIC}
