mapper.insert:
INSERT INTO EQUIPMENT_STATUS_RECORD
(
	EQUIPMENT_RECORD_KEY, 
	EQUIPMENT_KEY, 
	EQUIPMENT_RECORD_WRITER, 
	EQUIPMENT_RECORD_STATUS, 
	EQUIPMENT_RECORD_CONTENT, 
	EQUIPMENT_RECORD_DATE
)
VALUES
(
	EQUIPMENT_STATUS_RECORD_SEQ.NEXTVAL
	, #{equipmentKey, jdbcType=INTEGER}
	, #{equipmentRecordWriter, jdbcType=VARCHAR}
	, #{equipmentRecordStatus,jdbcType=VARCHAR}
	, #{equipmentRecordContent,jdbcType=VARCHAR}
	, SYSDATE
)

mapper.update:
UPDATE PT_EQUIPMENT 
SET PT_EQUIPMENT_STATUS = #{ptEquipmentStatus, jdbcType=VARCHAR}
WHERE PT_EQUIPMENT_KEY = #{ptEquipmentKey, jdbcType=INTEGER}

mapper.insert:
INSERT INTO PT_RECORD
(
	PT_RECORD_KEY, 
	PT_BED_KEY, 
	ORDER_KEY, 
	EQUIPMENT_KEY, 
	PT_RECORD_CONTENT, 
	EMP_KEY, 
	PT_RECORD_DATE
)
VALUES
(
	PT_RECORD_SEQ.NEXTVAL
	, #{ptBedKey, jdbcType=INTEGER}
	, #{orderKey, jdbcType=VARCHAR}
	, #{equipmentKey, jdbcType=INTEGER}
	, ''
	, #{empKey, jdbcType=VARCHAR}
	, SYSDATE
)

mapper.select:
SELECT P.PATIENT_NAME, P.PATIENT_AGE, P.PATIENT_SEX, R.RECEIPT_KEY, CC2.CODE_NAME BODY_PART_NAME, DI.DISEASE_NM_EN, (SELECT 'PT_R'|| LPAD(PR.PT_RECORD_KEY, 6, '0') FROM DUAL) PT_RECORD_CODE,
PT_RECORD_DATE, E.EMP_NAME, (SELECT EMP_NAME FROM EMP WHERE EMP.EMP_KEY = PR.EMP_KEY) AS PT_NAME FROM PATIENT P LEFT OUTER JOIN RECEIPT R ON P.PATIENT_KEY = R.PATIENT_KEY LEFT OUTER JOIN MEDICAL_CHART MC ON R.RECEIPT_KEY = MC.RECEIPT_KEY LEFT OUTER JOIN DIAGNOSIS DA ON DA.CHART_KEY = MC.CHART_KEY LEFT OUTER JOIN DISEASE DI ON DI.DISEASE_KEY = DA.DISEASE_KEY LEFT OUTER JOIN ORDER_HISTORY OH ON MC.CHART_KEY = OH.CHART_KEY LEFT OUTER JOIN EMP E ON E.EMP_KEY = MC.EMP_KEY LEFT OUTER JOIN COMMON_CODE CC2 ON CC2.CODE = OH.BODY_PART_KEY
LEFT OUTER JOIN PT_RECORD PR ON PR.ORDER_KEY = OH.ORDER_KEY WHERE OH.ORDER_KEY = #{orderKey}

mapper.update:
UPDATE PT_RECORD 
SET PT_RECORD_CONTENT = #{ptRecordContent, jdbcType=CLOB}
WHERE PT_RECORD_KEY = #{ptRecordKey, jdbcType=INTEGER}

UPDATE WAITING_HISTORY SET
	   WAITING_STATUS = 'WS03'
WHERE RECEIPT_KEY = #{receiptKey, jdbcType=VARCHAR}

UPDATE PT_EQUIPMENT SET 
PT_EQUIPMENT_STATUS = #{ptEquipmentStatus, jdbcType=VARCHAR}
WHERE PT_EQUIPMENT_CODE = #{ptEquipmentCode, jdbcType=VARCHAR}

UPDATE MEDICLA_EQUIPMENT SET
EQUIPMENT_STATUS = #{equipmentStatus, jdbcType=VARCHAR}
WHERE EQUIPMENT_KEY = #{equipmentKey, jdbcType=INTEGER}
